import './productList.css';
import { DataGrid } from '@material-ui/data-grid';
import { DeleteOutline } from '@material-ui/icons';
import React, { useEffect, useState } from 'react';

import { Link } from 'react-router-dom';

import Sidebar from '../../sidebar/Sidebar';
import Loader from '../../../layout/Loader';

import { useAlert } from 'react-alert';
import { useDispatch, useSelector } from 'react-redux';
import {
  getAdminProducts,
  deleteProduct,
  clearErrors,
} from '../../../../actions/productActions';
import { DELETE_PRODUCT_RESET } from '../../../../constants/productConstants';
const productRows = [
  {
    id: 1,
    name: 'Apple iphone',
    img: 'https://images.pexels.com/photos/6771900/pexels-photo-6771900.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500',
    stock: 123,
    status: 'active',
    price: 'Ksh 130,000',
  },
  {
    id: 2,
    name: 'Apple iphone',
    img: 'https://images.pexels.com/photos/6771900/pexels-photo-6771900.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500',
    stock: 123,
    status: 'active',
    price: 'Ksh 130,000',
  },
];

export default function ProductList({ history }) {
  const alert = useAlert();
  const dispatch = useDispatch();
  const [data, setData] = useState('mgoab');

  const { loading, error, products } = useSelector((state) => state.products);
  const { error: deleteError, isDeleted } = useSelector(
    (state) => state.product
  );
  useEffect(() => {
    dispatch(getAdminProducts());

    if (error) {
      alert.error(error);
      dispatch(clearErrors());
    }

    if (deleteError) {
      alert.error(deleteError);
      dispatch(clearErrors());
    }
    if (!loading && products) {
      const newProducts = products.map((item) => {
        const { _id, name, stock, seller, price } = item;

        return {
          id: _id,
          name,
          seller,
          stock,
          price,
        };
      });
      setData(newProducts);
    }
    productRows.map((item) => console.log(item.id));
    console.log(productRows);

    if (isDeleted) {
      alert.success('Product deleted successfully');
      history.push('/admin/products');
      dispatch({ type: DELETE_PRODUCT_RESET });
    }
  }, [dispatch, alert, error, deleteError, isDeleted, history]);

  const handleDelete = (id) => {
    dispatch(deleteProduct(id));
  };

  console.log(products);
  console.log(data);

  return (
    <>
      <Sidebar />
      {/* {loading ? (
        <Loader />
      ) : (
        <div className="productList">
          <DataGrid
            rows={products}
            disableSelectionOnClick
            columns={columns}
            pageSize={8}
            checkboxSelection
          />
        </div>
      )} */}
    </>
  );
}
